// ==UserScript==
// @name           8 tracks download link and skip tracks
// @namespace      Yamamaya
// @include        http://*8tracks.com/*/*
// @version        0.2.0
// ==/UserScript==

var executeBrowserContext = function(funcOrString) {
    var code = 'javascript:(' + encodeURIComponent(funcOrString.toString()) + ')();';
    location.href = code;
};

executeBrowserContext(function(){
	var Icon = {
		mp3 : 'data:image/png;base64,',
		m4a : 'data:image/png;base64,',
		mp4 : 'data:image/png;base64,',
		aac : 'data:image/png;base64,',
		fla : 'data:image/png;base64,',
		flac: 'data:image/png;base64,'
	};
	
	var EightTracks = {
		init: function(){
			document.body.addEventListener('DOMNodeInserted',this,false);
			document.body.addEventListener('click',this,true);
		},
		handleEvent: function(evt){
			var target = evt.target;
			switch(evt.type){
				case 'DOMNodeInserted':
					if(target.classList && target.classList.contains('track')){
						this.createLink(target);
					}
				return;
					
				case 'click':
					if(target.id === 'player_skip_button' && !evt.button){
						this.skip();
						evt.preventDefault();
						evt.stopPropagation();
					}
				return;
			}
		},
		createLink: function(target){
			var point = target.querySelector('.track_details') || target;
			var url = this.trackUrl;
			var format = url.split('.');
			format = format[format.length - 1];
			var div = document.createElement('div');
			var dStyle = div.style;
			dStyle.cssFloat = 'left';
			dStyle.marginRight = '20px';
			var a = document.createElement('a');
			if(/client_id/.test(format)){
				format = 'soundcloud';
				a.target = '_blank';
			}
			var icon = Icon[format.toLowerCase()] || Icon.mp3;
			var aStyle = a.style;
			aStyle.fontSize    = '13px';
			aStyle.paddingLeft = '20px';
			aStyle.background  = 'transparent url('+icon+') no-repeat';
			a.href = url;
			a.title = url;
			a.textContent = 'Download ('+format+')';
			//div.appendChild(a);
			//point.appendChild(div);			
		},
		skip: function(){
			TRAX.mixPlayer.next();
		},
		get trackUrl(){
			return TRAX.mixPlayer.track.get('track_file_stream_url');
		}
	};
	
	EightTracks.init();
});
